mapper.update:
UPDATE CART
SET
	CART_COUNT = #{count}
WHERE ITEM_NO = #{itemNo}
	AND USERID = #{userId}

mapper.select:
SELECT CART_COUNT 
FROM CART 
WHERE USERID = #{userId} 
  AND ITEM_NO = #{itemNo}

mapper.update:
UPDATE CART 
SET CART_COUNT = CART_COUNT + #{cartCount} 
WHERE ITEM_NO = #{itemNo} 
  AND USERID = #{userId}
