mapper.select:
	SELECT P_NO, P_WAY
    FROM PAY 
    WHERE P_NO = #{pNo}

mapper.insert:
INSERT INTO order_list (
    user_id, price, discount_amount, used_point, shipping_fee, pway, order_items
) VALUES (
    #{userId}, #{price}, #{discountAmount}, #{usedPoint}, #{shippingFee}, #{pWay}, #{orderItems}
)
